const screenshotOptions = {
  path,
  fullPage: options.fullPage || false,
  ...options
};

// Only add quality for JPEG format (not supported for PNG)
if (this.config.output.screenshotFormat === 'jpeg' || this.config.output.screenshotFormat === 'jpg') {
  if (this.config.output.screenshotQuality) {
    screenshotOptions.quality = this.config.output.screenshotQuality;
  }
} else {
  // Remove quality if it was passed in options for PNG
  delete screenshotOptions.quality;
}
